document.querySelector('#enviar').addEventListener('click',obtener);

document.querySelector('#enviar2').addEventListener('click' ,obtenerDos);

function obtener()//implementando ajax
{
	//console.log('hola campeon');

	const xhttp= new XMLHttpRequest();
	//console.log(xhttp);
	xhttp.open('get','archivo.txt',true);
	xhttp.send();

//  onreadystatechange: Define una función que se llamará cuando cambie la propiedad readyState
//  readyState: si es igual a 4 solicitud terminada y la respuesta está lista
// status: si es igual a 200 esta listo
	xhttp.onreadystatechange=function()
	{
	 if (this.readyState==4 && this.status==200) 
		{
        document.querySelector('#resultado').innerHTML+=this.responseText + '  con ajax '+'<br>';
		}

	}

}

function obtenerDos()//implementado fetch
{
	fetch('archivo.txt')
	.then(res=>res.text())
	.then(data=>{

     document.querySelector('#resultado').innerHTML+=data  + '  con fetch '+'<br>';
	
	})

}

