var dolar=document.getElementById('dolar');
dolar.addEventListener('click',function(){

mostrarDatos('dolar');
});

var euro=document.getElementById('euro');
euro.addEventListener('click',function(){
	mostrarDatos('euro');

});


function mostrarDatos(moneda){
 
 var url='https://mindicador.cl/api/';

  var xhttp= new XMLHttpRequest();
  xhttp.open('GET',url+moneda,true);
  xhttp.send();

   xhttp.onreadystatechange=function()
   {
	  	if (this.readyState==4 && this.status==200) 
	  	{
	  	  //console.log(this.responseText);

	  	  var dt=JSON.parse(this.responseText);
	  	   // console.log(dt.serie);
            var valort=document.getElementById('valort');

	       valort.innerHTMl='';

	       for(var i of dt.serie)
	       	console.log(i);
	       {
	       	valort.innerHTMl+=`
				<tr>
					 <td>${i.fecha}</td>
					 <td>${i.valor}</td>			    
				 </tr>
			`
	       }
	  	
           

	  	}
  }



}

