var boton=document.getElementById('boton');
boton.addEventListener('click',cargarJson);

var most=document.getElementById('datos');

function cargarJson()
{
	console.log('hola')

	var xhttp=new XMLHttpRequest();
	xhttp.open('GET','texto.json',true);
	xhttp.send();
	xhttp.onreadystatechange=function(){
	 if(this.readyState==4 && this.status==200){
	 	console.log(this.responseText);

	 	var jsn=JSON.parse(this.responseText);
	 	console.log(jsn);

	 	most.innerHTML='';

	 	for(var dj of jsn)
	 	{
	 		most.innerHTML+=`
	 		 <tr>
			    <td>${dj.id}</td>
			    <td>${dj.nombre}</td>
			    <td>${dj.email}</td>
			    <td>${dj.estado ?"activo" :"desactivado"}</td>
			  </tr>

	 		`
	 	}
	 }
	}

}